"use strict";document.addEventListener("DOMContentLoaded",function(e){(function(){let maskWrapper=document.querySelector(".numeral-mask-wrapper");for(let pin of maskWrapper.children){pin.onkeyup=function(e){if(/^\d$/.test(e.key)){if(pin.nextElementSibling){if(this.value.length===parseInt(this.attributes["maxlength"].value)){pin.nextElementSibling.focus()}}}else if(e.key==="Backspace"){if(pin.previousElementSibling){pin.previousElementSibling.focus()}}};pin.onkeypress=function(e){if(e.key==="-"){e.preventDefault()}}}const twoStepsForm=document.querySelector("#twoStepsForm");if(twoStepsForm){const fv=FormValidation.formValidation(twoStepsForm,{fields:{otp:{validators:{notEmpty:{message:"Please enter otp"}}}},plugins:{trigger:new FormValidation.plugins.Trigger,bootstrap5:new FormValidation.plugins.Bootstrap5({eleValidClass:"",rowSelector:".mb-3"}),submitButton:new FormValidation.plugins.SubmitButton,defaultSubmit:new FormValidation.plugins.DefaultSubmit,autoFocus:new FormValidation.plugins.AutoFocus}});const numeralMaskList=twoStepsForm.querySelectorAll(".numeral-mask");const keyupHandler=function(){let otpFlag=true,otpVal="";numeralMaskList.forEach(numeralMaskEl=>{if(numeralMaskEl.value===""){otpFlag=false;twoStepsForm.querySelector('[name="otp"]').value=""}otpVal=otpVal+numeralMaskEl.value});if(otpFlag){twoStepsForm.querySelector('[name="otp"]').value=otpVal}};numeralMaskList.forEach(numeralMaskEle=>{numeralMaskEle.addEventListener("keyup",keyupHandler)})}})()});